var SymAuth = (function(){
	var username = null, authToken = null, password = null;
	var encryptedPass = null;
	var login = function(username,password){
		//var username = "senthilkumar_muthuve"
		//var encryptedPass = "ZHAyMUJ1ZEBzaw==";
		var authData = username + ":"+ password;
		authToken = new Buffer(authData).toString('base64'); 
	}	
	var getToken = function(){
		if(authToken == null){
			console.log("authToken not initialized. login first");
		}
		return authToken;
	}
	

	return {
		username : username,
		getToken : getToken,
		login : login
	}
})();